package cn.xdf.me.otp.model.base;import java.util.List;import javax.persistence.CascadeType;import javax.persistence.Entity;import javax.persistence.OneToMany;import javax.persistence.OneToOne;import javax.persistence.OrderBy;import javax.persistence.PrimaryKeyJoinColumn;import javax.persistence.Table;import cn.xdf.me.otp.model.core.CoreCourseware;import cn.xdf.me.otp.model.template.TemplateContent;/** * 新教师提交课件 *  * @author Zaric * @date 2013-6-1 上午1:08:07 */@Entity@Table(name = "IXDF_OTP_COURSEWARE")@PrimaryKeyJoinColumn(name = "coursewareId")public class Courseware extends CoreCourseware {	/**	 * 	 */	private static final long serialVersionUID = 1L;	/**	 * 提交课件项	 */	private List<CoursewareItem> items;	/**	 * 对应流程步骤信息	 */	private TemplateContent templateContent;	/**	 * 提交课件项	 * 	 * @return	 */	@OneToMany(mappedBy = "courseware", cascade = { CascadeType.PERSIST,			CascadeType.REFRESH, CascadeType.REMOVE, CascadeType.MERGE })	@OrderBy("name asc")	public List<CoursewareItem> getItems() {		return items;	}	/**	 * 提交课件项	 * 	 * @param items	 */	public void setItems(List<CoursewareItem> items) {		this.items = items;	}	/**	 * 对应流程步骤信息	 * 	 * @return	 */	@OneToOne(mappedBy = "courseware")	public TemplateContent getTemplateContent() {		return templateContent;	}	/**	 * 对应流程步骤信息	 * 	 * @param templateContent	 */	public void setTemplateContent(TemplateContent templateContent) {		this.templateContent = templateContent;	}}