package cn.xdf.me.otp.model.flow;import java.util.List;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.PrimaryKeyJoinColumn;import javax.persistence.Table;import javax.persistence.Transient;import org.apache.commons.lang3.StringUtils;import cn.xdf.me.otp.model.core.CoreExam;import com.google.common.collect.ImmutableList;/** * 流程步骤记录 * <p/> *  * <pre> * 新教师针对考试进行答题记录 * </pre> *  * @author Zaric * @date 2013-6-1 上午1:15:01 */@Entity@Table(name = "IXDF_OTP_BAM_EXAM_ITEM_LOG")@PrimaryKeyJoinColumn(name = "bamExamId")public class BamExam extends CoreExam {	/**	 * 	 */	private static final long serialVersionUID = 1L;	/**	 * 答题内容	 */	private String examContent;	/**	 * 答案是否正确	 */	private boolean isRight;	/**	 * 考题类别	 */	private BamExamCategory bamExamCategory;	@Transient	public List<String> getAllRight() {		String answers = getFdAnswer();		return ImmutableList.copyOf(StringUtils.split(answers, ","));	}	/**	 * 答题内容	 * 	 * @return	 */	public String getExamContent() {		return examContent;	}	/**	 * 答题内容	 * 	 * @param examContent	 */	public void setExamContent(String examContent) {		this.examContent = examContent;	}	/**	 * 是否正确	 * 	 * @return	 */	@Column(name = "fdRight")	public boolean isRight() {		return isRight;	}	/**	 * 是否正确	 * 	 * @param right	 */	public void setRight(boolean right) {		isRight = right;	}	/**	 * 对应考题类别	 * 	 * @return	 */	@ManyToOne(fetch = FetchType.LAZY)	@JoinColumn(name = "fdBamCategoryId")	public BamExamCategory getBamExamCategory() {		return bamExamCategory;	}	/**	 * 对应考题类别	 * 	 * @param bamExamCategory	 */	public void setBamExamCategory(BamExamCategory bamExamCategory) {		this.bamExamCategory = bamExamCategory;	}}