package cn.xdf.me.otp.model.flow;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Table;import cn.xdf.me.otp.model.IdEntity;/** * 备课操作记录 只记录新教师和指导教师的交互操作 文本信息 *  * @author Zaric * @date 2013-6-1 上午1:15:49 */@Entity@Table(name = "IXDF_OTP_BAM_LOG")public class BamLog extends IdEntity {	/**	 * 	 */	private static final long serialVersionUID = 1L;	/**	 * 用户Id	 */	private String uid;	/**	 * 流程名称	 */	private String flowName;	/**	 * 阶段记录的Id	 */	private String stageId;	/**	 * 步骤记录的Id	 */	private String stepId;	/**	 * 操作记录	 */	private String logInfo;	/**	 * 用户id	 * 	 * @return	 * @see cn.xdf.me.otp.model.sys.SysOrgPerson fdId	 */	@Column(name = "FD_UID", length = 32)	public String getUid() {		return uid;	}	/**	 * 用户Id	 * 	 * @param uid	 * @see cn.xdf.me.otp.model.sys.SysOrgPerson fdId	 */	public void setUid(String uid) {		this.uid = uid;	}	/**	 * 流程记录名称	 * 	 * @return	 * @see cn.xdf.me.otp.model.flow.BamProject name	 */	public String getFlowName() {		return flowName;	}	/**	 * 流程记录名称	 * 	 * @param flowName	 * @see cn.xdf.me.otp.model.flow.BamProject name	 */	public void setFlowName(String flowName) {		this.flowName = flowName;	}	/**	 * 流程阶段Id	 * 	 * @return	 * @see cn.xdf.me.otp.model.flow.BamStage fdId	 */	public String getStageId() {		return stageId;	}	/**	 * 流程阶段id	 * 	 * @param stageId	 * @see cn.xdf.me.otp.model.flow.BamStage fdId	 */	public void setStageId(String stageId) {		this.stageId = stageId;	}	/**	 * 流程步骤id	 * 	 * @return	 * @see cn.xdf.me.otp.model.flow.BamProjectPhase fdId	 */	public String getStepId() {		return stepId;	}	/**	 * 流程步骤id	 * 	 * @param stepId	 * @see cn.xdf.me.otp.model.flow.BamProjectPhase fdId	 */	public void setStepId(String stepId) {		this.stepId = stepId;	}	/**	 * 操作信息 文本 信息	 * 	 * @return	 */	public String getLogInfo() {		return logInfo;	}	/**	 * 操作信息 文本信息	 * 	 * @param logInfo	 */	public void setLogInfo(String logInfo) {		this.logInfo = logInfo;	}}