package cn.xdf.me.otp.model.flow;import java.sql.Timestamp;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import javax.persistence.Transient;import org.hibernate.annotations.Cache;import org.hibernate.annotations.CacheConcurrencyStrategy;import cn.xdf.me.otp.model.IdEntity;import cn.xdf.me.otp.model.sys.SysOrgPerson;/** *  * 备课流程参与人 *  * <pre> * 此类第一作为流程选择人员的存储，第二作为流程进行中的人员流程存储。 *  * 根据新东方后期的业务梳理和需求， * 学校主管在选择流程参与的时候， * 1：针对新教师可以选择人员， * 2：针对新教师可以添加人员（添加用户的邮箱和用户名，然后由用户补充相应的信息） * 3：在每一行添加或者选择完新教师后，用户选择指导教师.(注意这里，选择的新教师必须未在此备课里添加过或者是流程通过备课) * </pre> *  * ------------------------------------------------------------------- *  * <pre> * 1：存储用户针对此流程的状态。 * 如果用户通过此流程，状态值为通过。 * </pre> *  * -------------------------------------------------------------------- *  * <pre> * 1：如果统计每个学校针对（项目、课程、分项、阶段）的通过率或者参与率 * 也可以进抽取此表的数据进行统计 * @see cn.xdf.me.otp.model.flow.BamProject * </pre> *  * @author Zaric * @date 2013-6-1 上午1:17:10 */@Entity@Table(name = "IXDF_OTP_BAM_PROJECT_MEMBER")@Cache(usage = CacheConcurrencyStrategy.READ_WRITE)public class BamProjectMember extends IdEntity {	/**	 * 	 */	private static final long serialVersionUID = 1L;	/**	 * 对应备课流程	 */	private BamProject project;	/**	 * 对应新教师ID	 */	private String newteachId;	/**	 * 当前阶段ID	 */	private String itemId;	/**	 * 当前步骤ID	 */	private String contentId;	/**	 * 指导教师ID	 */	private String guidId;	/**	 * 所在部门ID	 */	private String deptId;	/**	 * 所在机构(学校)	 */	private String schId;	/**	 * 冗余字段(项目ID)	 */	private String projectId;	/**	 * 冗余字段(课程ID)	 */	private String couurseId;	/**	 * 冗余字段(分项ID)	 */	private String subjectId;	/**	 * 冗余字段(阶段ID)	 */	private String stageId;	/**	 * 是否通过	 */	private boolean through;	/**	 * 对应新教师	 */	private SysOrgPerson newTeach;	/**	 * 对应指导教师	 */	private SysOrgPerson guid;	/**	 * 开始时间	 */	private Timestamp startTime;	/**	 * 结束时间	 */	private Timestamp endTime;	/**	 * 最终得分	 */	private Float finalScore;	/**	 * 批课时长	 */	private Float gradeDuration;	/**	 * 对应备课流程	 * 	 * @return	 */	@ManyToOne(fetch = FetchType.LAZY)	@JoinColumn(name = "fdProjectId")	public BamProject getProject() {		return project;	}	/**	 * 对应备课流程	 * 	 * @param project	 */	public void setProject(BamProject project) {		this.project = project;	}	/**	 * 新教师ID	 * 	 * @return	 */	@Column(length = 32)	public String getNewteachId() {		return newteachId;	}	/**	 * 新教师ID	 * 	 * @param newteachId	 */	public void setNewteachId(String newteachId) {		this.newteachId = newteachId;	}	/**	 * 当前阶段ID	 * 	 * @return	 */	@Column(length = 32)	public String getItemId() {		return itemId;	}	/**	 * 当前阶段ID	 * 	 * @param itemId	 */	public void setItemId(String itemId) {		this.itemId = itemId;	}	/**	 * 当前步骤ID	 * 	 * @return	 */	@Column(length = 32)	public String getContentId() {		return contentId;	}	/**	 * 当前步骤ID	 * 	 * @param contentId	 */	public void setContentId(String contentId) {		this.contentId = contentId;	}	/**	 * 指导教师ID	 * 	 * @return	 */	@Column(length = 32)	public String getGuidId() {		return guidId;	}	/**	 * 指导教师ID	 * 	 * @param guidId	 */	public void setGuidId(String guidId) {		this.guidId = guidId;	}	/**	 * 学校ID（部门ID）	 * 	 * @return	 */	@Column(length = 32)	public String getDeptId() {		return deptId;	}	/**	 * 学校ID（机构ID）	 * 	 * @param deptId	 */	public void setDeptId(String deptId) {		this.deptId = deptId;	}	/**	 * 学校ID（机构ID）	 * 	 * @return	 */	@Column(length = 32)	public String getSchId() {		return schId;	}	/**	 * 学校ID（机构ID）	 * 	 * @param schId	 */	public void setSchId(String schId) {		this.schId = schId;	}	/**	 * 项目ID（冗余字段）	 * 	 * @return	 */	@Column(length = 32)	public String getProjectId() {		return projectId;	}	/**	 * 项目ID（冗余字段）	 * 	 * @param projectId	 */	public void setProjectId(String projectId) {		this.projectId = projectId;	}	/**	 * 课程ID（冗余字段）	 * 	 * @return	 */	@Column(length = 32)	public String getCouurseId() {		return couurseId;	}	/**	 * 课程ID（冗余字段）	 * 	 * @param couurseId	 */	public void setCouurseId(String couurseId) {		this.couurseId = couurseId;	}	/**	 * 分项ID（冗余字段）	 * 	 * @return	 */	@Column(length = 32)	public String getSubjectId() {		return subjectId;	}	/**	 * 分项ID（冗余字段）	 * 	 * @param subjectId	 */	public void setSubjectId(String subjectId) {		this.subjectId = subjectId;	}	/**	 * 阶段ID(冗余字段)	 * 	 * @return	 */	@Column(length = 32)	public String getStageId() {		return stageId;	}	/**	 * 阶段ID(冗余字段)	 * 	 * @param stageId	 */	public void setStageId(String stageId) {		this.stageId = stageId;	}	/**	 * 是否通过	 * 	 * @return	 */	public boolean isThrough() {		return through;	}	/**	 * 是否通过	 * 	 * @param through	 */	public void setThrough(boolean through) {		this.through = through;	}	/**	 * 对应新教师	 * 	 * @return	 */	@Transient	public SysOrgPerson getNewTeach() {		return newTeach;	}	/**	 * 对应新教师	 * 	 * @param newTeach	 */	public void setNewTeach(SysOrgPerson newTeach) {		this.newTeach = newTeach;	}	/**	 * 对应指导教师	 * 	 * @return	 */	@Transient	public SysOrgPerson getGuid() {		return guid;	}	/**	 * 对应指导教师	 * 	 * @param guid	 */	public void setGuid(SysOrgPerson guid) {		this.guid = guid;	}	/**	 * 结束时间	 */	public Timestamp getEndTime() {		return endTime;	}	/**	 * 结束时间	 * 	 * @param endTime	 */	public void setEndTime(Timestamp endTime) {		this.endTime = endTime;	}	/**	 * 最终得分	 */	public Float getFinalScore() {		return finalScore;	}	/**	 * 最终得分	 * 	 * @param finalScore	 */	public void setFinalScore(Float finalScore) {		this.finalScore = finalScore;	}	/**	 * 开始时间	 */	public Timestamp getStartTime() {		return startTime;	}	/**	 * 开始时间	 * 	 * @param startTime	 */	public void setStartTime(Timestamp startTime) {		this.startTime = startTime;	}	/**	 * 批课时长	 */	public Float getGradeDuration() {		return gradeDuration;	}	/**	 * 批课时长	 * 	 * @param gradeDuration	 */	public void setGradeDuration(Float gradeDuration) {		this.gradeDuration = gradeDuration;	}	/**	 * 以下为报表所用字段	 */	private String newTeachName;	private String guidTeachName;	private String beginTem;	private String projectName;	private String templateName;	@Transient	public String getNewTeachName() {		return newTeachName;	}	public void setNewTeachName(String newTeachName) {		this.newTeachName = newTeachName;	}	@Transient	public String getGuidTeachName() {		return guidTeachName;	}	public void setGuidTeachName(String guidTeachName) {		this.guidTeachName = guidTeachName;	}	@Transient	public String getBeginTem() {		return beginTem;	}	public void setBeginTem(String beginTem) {		this.beginTem = beginTem;	}	@Transient	public String getProjectName() {		return projectName;	}	public void setProjectName(String projectName) {		this.projectName = projectName;	}	@Transient	public String getTemplateName() {		return templateName;	}	public void setTemplateName(String templateName) {		this.templateName = templateName;	}}