package cn.xdf.me.otp.model.flow;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import javax.persistence.Transient;import org.hibernate.annotations.Cache;import org.hibernate.annotations.CacheConcurrencyStrategy;import cn.xdf.me.otp.model.IdEntity;import cn.xdf.me.otp.model.sys.SysOrgPerson;/** * 新教师给批课老师打分 *  * @author Zaric * @date 2013-6-1 上午1:18:55 */@Entity@Table(name = "IXDF_OTP_BAM_TICKLING")@Cache(usage = CacheConcurrencyStrategy.READ_WRITE)public class Tickling extends IdEntity {	/**	 * 	 */	private static final long serialVersionUID = 1L;	/**	 * 批课打分表	 */	private Grade grade;	/**	 * 批课老师Id	 */	private String guidId;	/**	 * 新老师Id	 */	private String newTeachId;	/**	 * 新老师给批课老师评价	 */	private String appraise;	/**	 * 新教师给指导教师给的打分	 */	private Float value1;	/**	 * 新教师给指导教师给的打分	 */	private Float value2;	/**	 * 新教师给指导教师给的打分	 */	private Float value3;	/**	 * 新教师给指导教师给的打分	 */	private Float value4;	/**	 * 新教师给指导教师给的打分	 */	private Float value5;	/**	 * 总分	 */	private Float totalvalue;	/**	 * 批课打分表	 * 	 * @return	 */	@ManyToOne(fetch = FetchType.LAZY)	@JoinColumn(name = "gradeId")	public Grade getGrade() {		return grade;	}	public void setGrade(Grade grade) {		this.grade = grade;	}	/**	 * 批课老师	 * 	 * @return	 */	public String getGuidId() {		return guidId;	}	public void setGuidId(String guidId) {		this.guidId = guidId;	}	/**	 * 新教师老师	 * 	 * @return	 */	public String getNewTeachId() {		return newTeachId;	}	public void setNewTeachId(String newTeachId) {		this.newTeachId = newTeachId;	}	/**	 * 给批课老师综合评价	 * 	 * @return	 */	@Column(length = 500)	public String getAppraise() {		return appraise;	}	public void setAppraise(String appraise) {		this.appraise = appraise;	}	/**	 * 指标1打分	 * 	 * @return	 */	@Column(precision = 10, scale = 1)	public Float getValue1() {		return value1;	}	public void setValue1(Float value1) {		this.value1 = value1;	}	/**	 * 指标2打分	 * 	 * @return	 */	@Column(precision = 10, scale = 1)	public Float getValue2() {		return value2;	}	public void setValue2(Float value2) {		this.value2 = value2;	}	/**	 * 指标3打分	 * 	 * @return	 */	@Column(precision = 10, scale = 1)	public Float getValue3() {		return value3;	}	public void setValue3(Float value3) {		this.value3 = value3;	}	/**	 * 指标4打分	 * 	 * @return	 */	@Column(precision = 10, scale = 1)	public Float getValue4() {		return value4;	}	public void setValue4(Float value4) {		this.value4 = value4;	}	/**	 * 指标5打分	 * 	 * @return	 */	@Column(precision = 10, scale = 1)	public Float getValue5() {		return value5;	}	public void setValue5(Float value5) {		this.value5 = value5;	}	/**	 * 指标总分	 * 	 * @return	 */	@Column(precision = 10, scale = 1)	public Float getTotalvalue() {		return totalvalue;	}	public void setTotalvalue(Float totalvalue) {		this.totalvalue = totalvalue;	}	private SysOrgPerson newTeach;	@Transient	public SysOrgPerson getNewTeach() {		return newTeach;	}	public void setNewTeach(SysOrgPerson newTeach) {		this.newTeach = newTeach;	}	private SysOrgPerson advier;	@Transient	public SysOrgPerson getAdvier() {		return advier;	}	public void setAdvier(SysOrgPerson advier) {		this.advier = advier;	}}