package cn.xdf.me.otp.model.sys;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import org.hibernate.annotations.Cache;import org.hibernate.annotations.CacheConcurrencyStrategy;import cn.xdf.me.otp.model.IdEntity;/** * 新教师教育背景 *  * @author Zaric * @date 2013-6-1 上午1:20:42 */@Entity@Table(name = "IXDF_OTP_BAM_EDUCATION")@Cache(usage = CacheConcurrencyStrategy.READ_WRITE)public class EducationBackground extends IdEntity {	/**	 * 	 */	private static final long serialVersionUID = 1L;	/**	 * 注册表	 */	private SysOrgPersonTemp sysOrgPersonTemp;	/**	 * 学校名称	 */	private String schoolName;	/**	 * 入学时间	 */	private String beginYear;	/**	 * 专业名称	 */	private String majorName;	/**	 * 注册主表	 * 	 * @return	 */	@ManyToOne(fetch = FetchType.LAZY)	@JoinColumn(name = "personId")	public SysOrgPersonTemp getSysOrgPersonTemp() {		return sysOrgPersonTemp;	}	public void setSysOrgPersonTemp(SysOrgPersonTemp sysOrgPersonTemp) {		this.sysOrgPersonTemp = sysOrgPersonTemp;	}	public String getSchoolName() {		return schoolName;	}	public void setSchoolName(String schoolName) {		this.schoolName = schoolName;	}	public String getBeginYear() {		return beginYear;	}	public void setBeginYear(String beginYear) {		this.beginYear = beginYear;	}	public String getMajorName() {		return majorName;	}	public void setMajorName(String majorName) {		this.majorName = majorName;	}}