package ruyees.otp.action.admin;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@Controller@Scope("request")public class AdminController {	/**	 * 管理控制台	 * 	 * @return	 */	@RequestMapping(value = "/console/home", method = RequestMethod.GET)	public String init() {		return "admin";	}	@RequestMapping(value = "role/list")	public String listRole() {		return "/admin/rolelist";	}}