package ruyees.otp.action.ajax;import java.util.List;import javax.servlet.http.HttpServletRequest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import ruyees.otp.action.view.model.VContent;import ruyees.otp.service.flow.BamSeeCwService;/** * 查看视频 *  * @author Zaric * @date 2013-6-1 上午12:32:00 */@Controller@RequestMapping(value = "/ajax/cm")@Scope("request")public class ContentMovieAjaxController {	@Autowired	private BamSeeCwService bamSeeCwService;	@RequestMapping(value = "setThrough")	@ResponseBody	public List<VContent> setThrough(HttpServletRequest request) {		String key = request.getParameter("mvieId");		String nextId = request.getParameter("nextId");		return bamSeeCwService.setContentMovieThrough(key, nextId);	}}