package ruyees.otp.model.base;import ruyees.otp.model.core.CoreCoursewareItem;import javax.persistence.*;/** * 新教师提交课件项 * @author Zaric * @date  2013-6-1 上午1:08:23 */@Entity@Table(name = "IXDF_OTP_COURSEWARE_ITEM")@PrimaryKeyJoinColumn(name = "coursewareItemId")public class CoursewareItem extends CoreCoursewareItem {    /**	 * 	 */	private static final long serialVersionUID = 1L;		/**     * 对应提交课件父类     */    private Courseware courseware;    /**     * 对应提交课件     * @return     */    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "coursewareId")    public Courseware getCourseware() {        return courseware;    }    /**     * 对应提交课件     * @param courseware     */    public void setCourseware(Courseware courseware) {        this.courseware = courseware;    }}