package ruyees.otp.model.base;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.PrimaryKeyJoinColumn;import javax.persistence.Table;import org.hibernate.annotations.Cache;import org.hibernate.annotations.CacheConcurrencyStrategy;import ruyees.otp.model.core.CoreIndex;/** * 作业包指标 *  * @author Zaric * @date 2013-6-1 上午1:10:11 */@Entity@Table(name = "IXDF_OTP_INDEX")@Cache(usage = CacheConcurrencyStrategy.READ_WRITE)@PrimaryKeyJoinColumn(name = "indexId")public class Index extends CoreIndex {	/**	 * 	 */	private static final long serialVersionUID = 1L;	public static final String MODEL_NAME = "ruyees.otp.model.base.Index";	/**	 * 对应作业	 */	private Operation operation;	public Index() {	}	public Index(String fdId) {		this.fdId = fdId;	}	/**	 * 	 * 对应作业	 */	@ManyToOne(fetch = FetchType.LAZY)	@JoinColumn(name = "fdOperationId")	public Operation getOperation() {		return operation;	}	public void setOperation(Operation operation) {		this.operation = operation;	}	@Override	public int hashCode() {		final int prime = 11;		int result = 1;		result = prime * result + getFdId().hashCode();		return result;	}	@Override	public boolean equals(Object object) {		if (object == null) {			return false;		}		if (!getClass().equals(object.getClass())) {			return false;		}		Index other = (Index) object;		if (!this.fdId.equals(other.fdId)) {			return false;		}		return true;	}}