package ruyees.otp.model.demo;import javax.persistence.Entity;import javax.persistence.PrimaryKeyJoinColumn;import javax.persistence.Table;/** *  * @author Zaric * @date  2013-6-1 上午1:13:15 */@Entity@Table(name = "DEMO_MANAGER")@PrimaryKeyJoinColumn(name = "managerId")public class Manager extends Person {		/**	 * 	 */	private static final long serialVersionUID = 1L;		private String address;	public String getAddress() {		return address;	}	public void setAddress(String address) {		this.address = address;	}	@Override	public int hashCode() {		final int prime = 11;		int result = 1;		result = prime * result + getFdId().hashCode();		return result;	}	@Override	public boolean equals(Object obj) {		if (obj == null) {			return false;		}		if (!getClass().equals(obj.getClass())) {			return false;		}		final Manager other = (Manager) obj;		if (!this.fdId.equals(other.fdId)) {			return false;		}		return true;	}}