package ruyees.otp.model.demo;import javax.persistence.Entity;import javax.persistence.Inheritance;import javax.persistence.InheritanceType;import javax.persistence.Table;import ruyees.otp.model.IdEntity;/** *  * @author Zaric * @date  2013-6-1 上午1:13:33 */@Entity@Table(name = "demo_person")@Inheritance(strategy = InheritanceType.JOINED)public abstract class Person extends IdEntity {	/**	 * 	 */	private static final long serialVersionUID = 1L;	private String fdName;	public String getFdName() {		return fdName;	}	public void setFdName(String fdName) {		this.fdName = fdName;	}}