package ruyees.otp.model.sys;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import org.hibernate.annotations.Cache;import org.hibernate.annotations.CacheConcurrencyStrategy;import ruyees.otp.model.IdEntity;/** * 新教师工作经历 *  * @author Zaric * @date 2013-6-1 上午1:23:05 */@Entity@Table(name = "IXDF_OTP_BAM_WORKHISTORY")@Cache(usage = CacheConcurrencyStrategy.READ_WRITE)public class WorkHistory extends IdEntity {	/**	 * 	 */	private static final long serialVersionUID = 1L;	/**	 * 注册表	 */	private SysOrgPersonTemp sysOrgPersonTemp;	/**	 * 机构名称	 */	private String orgName;	/**	 * 部门/职位	 */	private String deptName;	/**	 * 工作开始时间	 */	private String beginYear;	/**	 * 工作结束时间	 */	private String endYear;	/**	 * 工作成果	 */	private String product;	/**	 * 注册主表	 * 	 * @return	 */	@ManyToOne(fetch = FetchType.LAZY)	@JoinColumn(name = "personId")	public SysOrgPersonTemp getSysOrgPersonTemp() {		return sysOrgPersonTemp;	}	public void setSysOrgPersonTemp(SysOrgPersonTemp sysOrgPersonTemp) {		this.sysOrgPersonTemp = sysOrgPersonTemp;	}	/**	 * 机构	 * 	 * @return	 */	public String getOrgName() {		return orgName;	}	public void setOrgName(String orgName) {		this.orgName = orgName;	}	/**	 * 部门/职位	 * 	 * @return	 */	public String getDeptName() {		return deptName;	}	public void setDeptName(String deptName) {		this.deptName = deptName;	}	/**	 * 开始年份	 * 	 * @return	 */	public String getBeginYear() {		return beginYear;	}	public void setBeginYear(String beginYear) {		this.beginYear = beginYear;	}	/**	 * 结束年份	 * 	 * @return	 */	public String getEndYear() {		return endYear;	}	public void setEndYear(String endYear) {		this.endYear = endYear;	}	/**	 * 工作成果	 * 	 * @return	 */	@Column(length = 500)	public String getProduct() {		return product;	}	public void setProduct(String product) {		this.product = product;	}}